svg.script:


const test = new Obj(document.getElementById('test'), { translate: [1000, 100] });
const test2 = new Obj(document.getElementById('test2'), { translate: [200, 200], scale: 1 });

const slide1 = new Slide();
slide1.play = () => {
    step({
        objects: [{
            object: test,
            transform: { translate: [600, 300], rotate: 45 },
        }],
    }).then(() => {
        step({
            objects: [{
                object: test2,
                transform: { translate: [800, 200], rotate: 720 },
            }],
        }).then(() => {
            next();
        });
    });
};

const slide2 = new Slide();
slide2.play = () => {
    step({
        objects: [{
            object: test,
            transform: { translate: [1000, 300], rotate: 360 },
            attributes: [
                { name: 'stroke-width', to: 10 },
            ],
        }],
    }).then(() => { next(); });
};

addSlides(slide1, slide2);
